import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:google_fonts/google_fonts.dart';import 'package:intl_phone_field/intl_phone_field.dart';import 'package:crud/api.dart';void main() {  runApp(SignUp());}class SignUp extends StatefulWidget {  const SignUp({super.key});  @override  State<SignUp> createState() => _SignUpState();}class _SignUpState extends State<SignUp> {  final node1=FocusNode();  final node2=FocusNode();  final node3=FocusNode();  final node4=FocusNode();  final node5=FocusNode();  final username=TextEditingController();  final newpassword=TextEditingController();  final confirmpassword=TextEditingController();  final emailid=TextEditingController();  final phone_number=TextEditingController();  final globalkey2=GlobalKey<FormState>();  final globalkey3=GlobalKey<FormState>();  final globalkey4=GlobalKey<FormState>();  @override  Widget build(BuildContext context) {    double height=MediaQuery.of(context).size.height;        double width=MediaQuery.of(context).size.width;    return      Scaffold(        floatingActionButtonLocation: FloatingActionButtonLocation.centerFloat,        floatingActionButton: FloatingActionButton.extended(          elevation: 30,          hoverColor: Colors.red,          splashColor: Colors.greenAccent,          backgroundColor: Color(0xff363062),          onPressed: ()async{            if(globalkey2.currentState!.validate()            ){            await api.postRequestSignUp(username.text,                newpassword.text,                phone_number.text,                emailid.text);            }          },        label:Text("Sign Up",          style: GoogleFonts.abrilFatface(fontSize: 0.04*width,              color: Colors.white),),          isExtended:true,          icon: Icon(Icons.person,color: Colors.white,),        ),      resizeToAvoidBottomInset: false,      backgroundColor: Color(0xffA7EDE7),      body: Container(        padding: EdgeInsets.all(0.1*width),        child:        Form(          key: globalkey2,          child:        Column(          children: [            SizedBox(              height:0.05*height,            ),            TextFormField(            controller: username            ,              onChanged: (va){                globalkey2.currentState!.validate();              },              focusNode: node1,              decoration:InputDecoration(                labelText: "New username",                enabledBorder: OutlineInputBorder(                  borderRadius: BorderRadius.circular(10)                ),                border: OutlineInputBorder(                  borderRadius: BorderRadius.circular(10)                )              ),              validator: (value){                if(value==null || value.isEmpty || value.length<3){                  return "Enter username";                }                return null;              },              onFieldSubmitted: (value){                FocusScope.of(context).requestFocus(node2);              },            ),            SizedBox(height: 0.03*height,),            TextFormField(              controller: newpassword,              onFieldSubmitted: (v){                FocusScope.of(context).requestFocus(node3);},              focusNode: node2,                decoration:InputDecoration(                  labelText: "New Password",                    border: OutlineInputBorder(                      borderRadius: BorderRadius.circular(10))),              style: GoogleFonts.abrilFatface(              ),              validator: (value){                if(value==null || value.isEmpty){                  return "Enter password";                }                return null;              },),            SizedBox(height: 0.03*height,),            TextFormField(              onFieldSubmitted: (v){                FocusScope.of(context).requestFocus(node4);              },              focusNode: node3,              obscureText: true,              decoration:InputDecoration(                  labelText: "Confirm Password",                  border: OutlineInputBorder(                      borderRadius: BorderRadius.circular(10)                  )              ),              style: GoogleFonts.abrilFatface(              ),              validator: (value){                if(value==null || value.isEmpty){                  return "Enter password";                }                return null;              },            ),            SizedBox(height: 0.03*height,            ),            IntlPhoneField(              validator: (value){                if(value==null||value==0){                  return "Please enter valid email";                }                return null;              },              controller: phone_number,              decoration: InputDecoration(                labelText: 'Phone Number',                border: OutlineInputBorder(                  borderSide: BorderSide(),                ),              ),              initialCountryCode: 'IN',              onChanged: (phone) {                print(phone.completeNumber);              },            ),            SizedBox(height: 0.03*height,),            TextFormField(              controller: emailid,              onFieldSubmitted: (v){              },              validator: (value){                if(value==null || value.isEmpty || !value.contains("@gmail.com")){                  return "Please enter valid email";                }                return null;              },              onChanged: (v){                  globalkey4.currentState!.validate();              },              focusNode: node5,              decoration:InputDecoration(                  labelText: "Your Email id",                  isCollapsed:false,                  border: OutlineInputBorder(                      borderRadius: BorderRadius.circular(10)                  )              ),              style: GoogleFonts.abrilFatface(                  color: Colors.white              )),          SizedBox(height: 0.03*height,),            ]),        ),)      );  }}